		QueryPerformanceFrequency(&li);
		freq=li.QuadPart;
		QueryPerformanceCounter(&li);
		double current=1000.*(li.QuadPart-nticks)/freq;
		if(nCalls==200)
			best=current, nCalls=0, odd=!odd;
		else if(current<best)
			best=current;
		int bkMode=getBkMode();
