	//	const char *hwacc=usingOpenGL?"OpenGL":"Software";
		GUIPrint(x, y, odd?"[%.10fbest], %.10ffps, %d, %s, %s":"%.10fbest, %.10ffps, %d, %s, %s",
			1000/best,
			1000/current,
			nCalls,
			simd_method==2?"AVX":simd_method==1?"SSE2":"IA32",
			usingOpenGL?"OpenGL":"Software");
