	bool disc_c_divide_i			(Value const &x0, Value const &x1)
	{
		double const pole[2]={0, 0};
		if(x0.r<0)
		{
			if(x0.i<0)
			{
				if(x1.r<0)	return false;
				if(x1.r>0)	return _2d_between(x0.r, x0.i, pole[0], pole[1], x1.r, x1.i);
							return x1.i==0;
			}
			if(x0.i>0)
			{
				if(x1.r<0)	return false;
				if(x1.r>0)	return _2d_between(x0.r, x0.i, pole[0], pole[1], x1.r, x1.i);
							return x1.i==0;
			}
			{
				if(x1.r<0)	return false;
				if(x1.r>0)	return x1.i==0;
							return true;
			}
		}
		if(x0.r>0)
		{
			if(x0.i<0)
			{
				if(x1.r<0)	return _2d_between(x0.r, x0.i, pole[0], pole[1], x1.r, x1.i);
				if(x1.r>0)	return false;
							return x1.i==0;
			}
			if(x0.i>0)
			{
				if(x1.r<0)	return _2d_between(x0.r, x0.i, pole[0], pole[1], x1.r, x1.i);
				if(x1.r>0)	return false;
							return x1.i==0;
			}
			{
				if(x1.r<0)	return x1.i==0;
				if(x1.r>0)	return false;
							return true;
			}
		}
		{
			if(x0.i<0)
			{
				if(x1.r<0)	return false;
				if(x1.r>0)	return false;
							return x1.i>=0;
			}
			if(x0.i>0)
			{
				if(x1.r<0)	return false;
				if(x1.r>0)	return false;
							return x1.i<=0;
			}
			{
							return true;
			}
		}
	}
