	inline double power_real(double const &x, int y)
	{
		double m[]={1, 0}, r=1;
		if(y<0)
			m[1]=1/x, y=-y;
		else
			m[1]=x;
		for(;;)
		{
			r*=m[y&1], y>>=1;
			if(!y)
				return r;
			m[1]*=m[1];
		}
		return r;
	}
	inline Quat1d power_real(Quat1d const &x, int y)
	{
		Quat1d t=x, r(1.);
		int p=abs(y);
		for(;;)
		{
			if(p&1)
				r*=t;
			p>>=1;
			if(!p)
				return y<0?inv(r):r;
			t*=t;
		}
		return y<0?inv(r):r;
	}
