	Quat1d tetrate(Quat1d &x, double y)
	{
		Quat1d qx=x;
		double ry=y;
		if(ry<-1)
		{
			int steps=int(abs(ry));
			Quat1d t(ry-::floor(ry)), lrx=log(qx);
			for(int k=0;k<steps;++k)
				t=log(t)/lrx;
			return t;
		}
		else if(ry<=0)
			return Quat1d(1+ry);
		else
		{
			int h=int(ry)+1;
			Quat1d t(ry-::floor(ry));
			for(int k=0;k<h;++k)
				t=pow(qx, t);
			return t;
		}
	}
