	{
		if(x0.r==x1.r)
		{
			if(x0.i==x1.i)	return false;
			double t=x0.r/_pi-.5;
			return t==std::floor(t);
		}
		if(x0.i==x1.i)	return x0.i==0&&_1d_int_in_range(x0.r/_pi-.5, x1.r/_pi-.5);
		if(std::signbit(x0.i)!=std::signbit(x1.i))
		{
			double t=_1d_zero_crossing(x0.r, x0.i, x1.r, x0.i)/_pi-.5;
			return t==std::floor(t);
		}
		return false;
	}
