struct CallInfo
{
	int i;//call address		index in instructions vector
	Expression *func;

	std::vector<Value> fData;
	CallInfo(Expression *func, int i):func(func), i(i){}
};
struct Solve_UserFunction
{
	Expression &expr;
	Instruction &in;
	bool operate_on_const;
	Solve_UserFunction(Expression &expr, Instruction &in, bool operate_on_const):expr(expr), in(in), operate_on_const(operate_on_const){}
	void operator()(int v)const
	{
		auto func=&userFunctionDefinitions[in.op1];
		std::vector<Value> fData(func->data.size());
		{
			int k=0;
			if(operate_on_const)
			{
				for(int kEnd=in.args.size();k<kEnd;++k)//copy args
					fData[k]=expr.data[in.args[k]];
			}
			else
			{
				for(int kEnd=in.args.size();k<kEnd;++k)//copy args
				{
					auto &n=expr.n[in.args[k]];
					//if(v>=n.r.size()<<1||n.mathSet=='c'&&v>=n.i.size()<<1)//
					//	int LOL_1=0;
					auto &dk=fData[k];
					dk.r=n.r[v];
					if(n.mathSet>='c')
					{
						dk.i=n.i[v];
						if(n.mathSet=='h')
						{
							dk.j=n.j[v];
							dk.k=n.k[v];
						}
					}
				}
			}
			for(int kEnd=func->data.size();k<kEnd;++k)//initialize const data
				fData[k]=func->data[k];
		}
		int startTime=clock(), expiryTime=startTime+userFunctionTimeLimit, offset=(startTime>0&&expiryTime<0)*userFunctionTimeLimit;
		std::stack<CallInfo> callStack;
		for(int i2=0, nInstr=func->i.size();;)
		{
			if(i2>=nInstr)//return nothing	unreachable
			{
				if(callStack.size())
				{
					auto &cst=callStack.top();
					func=cst.func;
					i2=cst.i, nInstr=func->i.size();
					cst.fData[func->i[i2].result].setzero();
				//	cst.fData[func->i[i2].result]=fData[0];//
					std::swap(cst.fData, fData);
					callStack.pop();
					continue;
				}
				if(operate_on_const)
					expr.data[in.result].setzero();
				else
					expr.n[in.result].assign(v, Value());
				break;
			}
			if(clock()+offset>expiryTime)
			{
				func->functionStuck=true;//mark function

				//return nan
				if(callStack.size())
				{
					auto &cst=callStack.top();
					func=cst.func;
					i2=cst.i, nInstr=func->i.size();
					cst.fData[func->i[i2].result]=G2::_qnan;
					std::swap(cst.fData, fData);
					callStack.pop();
					++i2;
					continue;
				}
				if(operate_on_const)
					expr.data[in.result].setnan();
				//	expr.data[in.result]=G2::_qnan;
				else
					expr.n[in.result].assign(v, G2::_qnan);
				break;
			}
			auto &in2=func->i[i2];
		//	func_data_to_clipboard(fData);
			switch(in2.type)
			{
			case 'c'://call user function
				if(markFunctionsStuck&&userFunctionDefinitions[in2.op1].functionStuck)//return nan without call
				{
					fData[in2.result]=G2::_qnan;
					++i2;
				}
				else
				{
					callStack.push(CallInfo(func, i2));
					auto &cst=callStack.top();
					cst.fData=std::move(fData);
				//	func=in2.function;
					func=&userFunctionDefinitions[in2.op1], i2=0;
					fData.resize(func->data.size());
					{
						int k=0;
						for(int kEnd=in2.args.size();k<kEnd;++k)//copy args
							fData[k]=cst.fData[in2.args[k]];
						//	fData[k]=cst.func->data[in2.args[k]];
						for(int kEnd=func->data.size();k<kEnd;++k)//initialize const data
							fData[k]=func->data[k];
					}
				}
				continue;
			case 'b'://branch if
				if(fData[in2.op1].q_isTrue())
					i2=in2.result;
				else
					++i2;
				continue;
			case 'B'://branch if not
				if(!fData[in2.op1].q_isTrue())
					i2=in2.result;
				else
					++i2;
				continue;
			case 'j'://jump
				i2=in2.result;
				continue;
			case 'r'://return
				if(callStack.size())
				{
					auto &cst=callStack.top();
					func=cst.func;
					i2=cst.i, nInstr=func->i.size();
					cst.fData[func->i[i2].result]=fData[in2.result];
					std::swap(cst.fData, fData);
					callStack.pop();
					++i2;
					continue;
				}
				if(operate_on_const)
					expr.data[in.result]=fData[in2.result];
				else
					expr.n[in.result].assign(v, fData[in2.result], func->resultMathSet);
				break;
			case -1:
				fData[in2.result].setzero();
				++i2;
				continue;
			case 1://r_r
				in2.ia32.r_r(VectP(&fData[in2.result].r), VectP(&fData[in2.op1].r));
				++i2;
				continue;
			case 2://c_c
				{
					auto &res=fData[in2.result], &op1=fData[in2.op1];
					in2.ia32.c_c(CompP(&res.r, &res.i), CompP(&op1.r, &op1.i));
					++i2;
				}
				continue;
			case 3://q_q
				{
					auto &res=fData[in2.result], &op1=fData[in2.op1];
					in2.ia32.q_q(QuatP(&res.r, &res.i, &res.j, &res.k), QuatP(&op1.r, &op1.i, &op1.j, &op1.k));
					++i2;
				}
				continue;
			case 4://r_rr
				{
					auto &res=fData[in2.result], &op1=fData[in2.op1], &op2=fData[in2.op2];
					in2.ia32.r_rr(VectP(&res.r), VectP(&op1.r), VectP(&op2.r));
					++i2;
				}
				continue;
			case 5://c_rc
				{
					auto &res=fData[in2.result], &op1=fData[in2.op1], &op2=fData[in2.op2];
					in2.ia32.c_rc(CompP(&res.r, &res.i), VectP(&op1.r), CompP(&op2.r, &op2.i));
					++i2;
				}
				continue;
			case 6://q_rq
				{
					auto &res=fData[in2.result], &op1=fData[in2.op1], &op2=fData[in2.op2];
					in2.ia32.q_rq(QuatP(&res.r, &res.i, &res.j, &res.k), VectP(&op1.r), QuatP(&op2.r, &op2.i, &op2.j, &op2.k));
					++i2;
				}
				continue;
			case 7://c_cr
				{
					auto &res=fData[in2.result], &op1=fData[in2.op1], &op2=fData[in2.op2];
					in2.ia32.c_cr(CompP(&res.r, &res.i), CompP(&op1.r, &op1.i), VectP(&op2.r));
					++i2;
				}
				continue;
			case 8://c_cc
				{
					auto &res=fData[in2.result], &op1=fData[in2.op1], &op2=fData[in2.op2];
					in2.ia32.c_cc(CompP(&res.r, &res.i), CompP(&op1.r, &op1.i), CompP(&op2.r, &op2.i));
					++i2;
				}
				continue;
			case 9://q_cq
				{
					auto &res=fData[in2.result], &op1=fData[in2.op1], &op2=fData[in2.op2];
					in2.ia32.q_cq(QuatP(&res.r, &res.i, &res.j, &res.k), CompP(&op1.r, &op1.i), QuatP(&op2.r, &op2.i, &op2.j, &op2.k));
					++i2;
				}
				continue;
			case 10://q_qr
				{
					auto &res=fData[in2.result], &op1=fData[in2.op1], &op2=fData[in2.op2];
					in2.ia32.q_qr(QuatP(&res.r, &res.i, &res.j, &res.k), QuatP(&op1.r, &op1.i, &op1.j, &op1.k), VectP(&op2.r));
					++i2;
				}
				continue;
			case 11://q_qc
				{
					auto &res=fData[in2.result], &op1=fData[in2.op1], &op2=fData[in2.op2];
					in2.ia32.q_qc(QuatP(&res.r, &res.i, &res.j, &res.k), QuatP(&op1.r, &op1.i, &op1.j, &op1.k), CompP(&op2.r, &op2.i));
					++i2;
				}
				continue;
			case 12://q_qq
				{
					auto &res=fData[in2.result], &op1=fData[in2.op1], &op2=fData[in2.op2];
					in2.ia32.q_qq(QuatP(&res.r, &res.i, &res.j, &res.k), QuatP(&op1.r, &op1.i, &op1.j, &op1.k), QuatP(&op2.r, &op2.i, &op2.j, &op2.k));
					++i2;
				}
				continue;

			case 13://c_r
				{
					auto &res=fData[in2.result], &op1=fData[in2.op1];
					in2.ia32.c_r(CompP(&res.r, &res.i), VectP(&op1.r));
					++i2;
				}
				continue;
			case 14://c_q
				{
					auto &res=fData[in2.result], &op1=fData[in2.op1];
					in2.ia32.c_q(CompP(&res.r, &res.i), QuatP(&op1.r, &op1.i, &op1.j, &op1.k));
					++i2;
				}
				continue;

			case 15://r_c
				{
					auto &res=fData[in2.result], &op1=fData[in2.op1];
					in2.ia32.r_c(VectP(&res.r), CompP(&op1.r, &op1.i));
					++i2;
				}
				continue;
			case 16://r_q
				{
					auto &res=fData[in2.result], &op1=fData[in2.op1];
					in2.ia32.r_q(VectP(&res.r), QuatP(&op1.r, &op1.i, &op1.j, &op1.k));
					++i2;
				}
				continue;

			case 17://c_rr
				{
					auto &res=fData[in2.result], &op1=fData[in2.op1], &op2=fData[in2.op2];
					in2.ia32.c_rr(CompP(&res.r, &res.i), VectP(&op1.r), VectP(&op2.r));
					++i2;
				}
				continue;

			case 18://r_rc
				{
					auto &res=fData[in2.result], &op1=fData[in2.op1], &op2=fData[in2.op2];
					in2.ia32.r_rc(VectP(&res.r), VectP(&op1.r), CompP(&op2.r, &op2.i));
					++i2;
				}
				continue;
			case 19://r_rq
				{
					auto &res=fData[in2.result], &op1=fData[in2.op1], &op2=fData[in2.op2];
					in2.ia32.r_rq(VectP(&res.r), VectP(&op1.r), QuatP(&op2.r, &op2.i, &op2.j, &op2.k));
					++i2;
				}
				continue;
			case 20://r_cr
				{
					auto &res=fData[in2.result], &op1=fData[in2.op1], &op2=fData[in2.op2];
					in2.ia32.r_cr(VectP(&res.r), CompP(&op1.r, &op1.i), VectP(&op2.r));
					++i2;
				}
				continue;
			case 21://r_cc
				{
					auto &res=fData[in2.result], &op1=fData[in2.op1], &op2=fData[in2.op2];
					in2.ia32.r_cc(VectP(&res.r), CompP(&op1.r, &op1.i), CompP(&op2.r, &op2.i));
					++i2;
				}
				continue;
			case 22://r_cq
				{
					auto &res=fData[in2.result], &op1=fData[in2.op1], &op2=fData[in2.op2];
					in2.ia32.r_cq(VectP(&res.r), CompP(&op1.r, &op1.i), QuatP(&op2.r, &op2.i, &op2.j, &op2.k));
					++i2;
				}
				continue;
			case 23://r_qr
				{
					auto &res=fData[in2.result], &op1=fData[in2.op1], &op2=fData[in2.op2];
					in2.ia32.r_qr(VectP(&res.r), QuatP(&op1.r, &op1.i, &op1.j, &op1.k), &op2.r);
					++i2;
				}
				continue;
			case 24://r_qc
				{
					auto &res=fData[in2.result], &op1=fData[in2.op1], &op2=fData[in2.op2];
					in2.ia32.r_qc(VectP(&res.r), QuatP(&op1.r, &op1.i, &op1.j, &op1.k), CompP(&op2.r, &op2.i));
					++i2;
				}
				continue;
			case 25://r_qq
				{
					auto &res=fData[in2.result], &op1=fData[in2.op1], &op2=fData[in2.op2];
					in2.ia32.r_qq(VectP(&res.r), QuatP(&op1.r, &op1.i, &op1.j, &op1.k), QuatP(&op2.r, &op2.i, &op2.j, &op2.k));
					++i2;
				}
				continue;
				
			case 26://c_qc
				{
					auto &res=fData[in2.result], &op1=fData[in2.op1], &op2=fData[in2.op2];
					in2.ia32.c_qc(CompP(&res.r, &res.i), QuatP(&op1.r, &op1.i, &op1.j, &op1.k), CompP(&op2.r, &op2.i));
					++i2;
				}
				continue;

			case 27://a ? b : c
				{
					auto &res=fData[in2.result], &op1=fData[in2.op1], &op2=fData[in2.op2], &op3=fData[in2.op3];
					char r_ms=maximum(in2.op2_ms, in2.op3_ms), op_ms=(in2.op2_ms=='c')+2*(in2.op2_ms=='h')+3*(in2.op3_ms=='c')+6*(in2.op3_ms=='h');
					switch(op_ms)
					{
					case 0:res.r=op1.r?op2.r:op3.r;break;//r_rr
					case 1:CompP(&res.r, &res.i)				=istrue(Comp1d(op1.r, op1.i))				?Comp1d(op2.r)						:Comp1d(op3.r, op3.i);				break;//c_rc
					case 2:QuatP(&res.r, &res.i, &res.j, &res.k)=istrue(Quat1d(op1.r, op1.i, op1.j, op1.k))	?Quat1d(op2.r)						:Quat1d(op3.r, op3.i, op3.j, op3.k);break;//q_rq
					case 3:CompP(&res.r, &res.i)				=istrue(Comp1d(op1.r, op1.i))				?Comp1d(op2.r, op2.i)				:Comp1d(op3.r);						break;//c_cr
					case 4:CompP(&res.r, &res.i)				=istrue(Comp1d(op1.r, op1.i))				?Comp1d(op2.r, op2.i)				:Comp1d(op3.r, op3.i);				break;//c_cc
					case 5:QuatP(&res.r, &res.i, &res.j, &res.k)=istrue(Quat1d(op1.r, op1.i, op1.j, op1.k))	?Quat1d(op2.r, op2.i)				:Quat1d(op3.r, op3.i, op3.j, op3.k);break;//q_cq
					case 6:QuatP(&res.r, &res.i, &res.j, &res.k)=istrue(Quat1d(op1.r, op1.i, op1.j, op1.k))	?Quat1d(op2.r, op2.i, op2.j, op2.k)	:Quat1d(op3.r);						break;//q_qr
					case 7:QuatP(&res.r, &res.i, &res.j, &res.k)=istrue(Quat1d(op1.r, op1.i, op1.j, op1.k))	?Quat1d(op2.r, op2.i, op2.j, op2.k)	:Quat1d(op3.r, op3.i);				break;//q_qc
					case 8:QuatP(&res.r, &res.i, &res.j, &res.k)=istrue(Quat1d(op1.r, op1.i, op1.j, op1.k))	?Quat1d(op2.r, op2.i, op2.j, op2.k)	:Quat1d(op3.r, op3.i, op3.j, op3.k);break;//q_qq
					}
				}
				++i2;
				continue;
			}
			break;
		}
	}
};
