	case M_MODULO_PERCENT:																					return  2;
	case M_BITWISE_NOT:																						return  3;
	case M_PENTATE:																							return  4;
	case M_TETRATE:																							return  5;
	case M_POWER:case M_POWER_REAL:																			return  6;
	case M_N:																								return  7;
