			case  5:function.ia32.c_rc(CompP(&x.r, &x.i),				VectP(&x.r),					CompP(&y.r, &y.i));				break;//c_rc
			case  6:function.ia32.q_rq(QuatP(&x.r, &x.i, &x.j, &x.k),	VectP(&x.r),					QuatP(&y.r, &y.i, &y.j, &y.k));	break;//q_rq
			case  7:function.ia32.c_cr(CompP(&x.r, &x.i),				CompP(&x.r, &x.i),				VectP(&y.r));					break;//c_cr
