			case  9:function.ia32.q_cq(QuatP(&x.r, &x.i, &x.j, &x.k),	CompP(&x.r, &x.i),				QuatP(&y.r, &y.i, &y.j, &y.k));	break;//q_cq
			case 10:function.ia32.q_qr(QuatP(&x.r, &x.i, &x.j, &x.k),	QuatP(&x.r, &x.i, &x.j, &x.k),	VectP(&y.r));					break;//q_qr
			case 11:function.ia32.q_qc(QuatP(&x.r, &x.i, &x.j, &x.k),	QuatP(&x.r, &x.i, &x.j, &x.k),	CompP(&y.r, &y.i));				break;//q_qc
			case 12:function.ia32.q_qq(QuatP(&x.r, &x.i, &x.j, &x.k),	QuatP(&x.r, &x.i, &x.j, &x.k),	QuatP(&y.r, &y.i, &y.j, &y.k));	break;//q_qq
			//case 13:function.ia32.c_r(CompP(&x.r, &x.i),	VectP(&x.r));					break;//c_r
			//case 14:function.ia32.c_q(CompP(&x.r, &x.i),	QuatP(&x.r, &x.i, &x.j, &x.k));	break;//c_q +
			//case 15:function.ia32.r_c(VectP(&x.r),		CompP(&x.r, &x.i));				break;//r_c +
			//case 16:function.ia32.r_q(VectP(&x.r),		QuatP(&x.r, &x.i, &x.j, &x.k));	break;//r_q +
			case 17:function.ia32.c_rr(CompP(&x.r, &x.i),	VectP(&x.r),					VectP(&y.r));					break;//c_rr
			case 18:function.ia32.r_rc(VectP(&x.r),			VectP(&x.r),					CompP(&y.r, &y.i));				break;//r_rc
