		bool next_AR(double &x)//call snap2grid_AR first to assign AR
		{
			bool change;
			x/=AR;
			if(logscale)
			{
				if(z_level)
					change=next_log_linstep(x);
				else
					change=next_log(x);
			}
			else
				change=next_linear(x);
			x*=AR;
			return change;
		}
