			int x1=a[0], x2=a[1], y1=a[2], y2=a[3], z1=a[4], z2=a[5];
			modes::Xstart=Xstart, modes::Xsample=Xsample, modes::Ystart=Ystart, modes::Ysample=Ysample, modes::Zstart=Zstart, modes::Zsample=Zsample, modes::T=T;
