		std::map<unsigned, std::unordered_map<int, std::list<std::pair<contour::Double_X_Y, contour::Double_X_Y>>>>
			contours;//contours[e]=contour, contour[z level (z/step)]=sticks, *stick.begin()=((x,y),(x,y))
		
